import { Component, OnInit } from "@angular/core";
import {
  trigger,
  state,
  style,
  animate,
  transition,
} from "@angular/animations";

@Component({
  selector: "app-mobile-header",
  templateUrl: "./mobile-header.component.html",
  styleUrls: ["./mobile-header.component.scss"],
  animations: [
    trigger("expandCollapse", [
      state(
        "collapsed",
        style({
          height: "0",
          opacity: "0",
          overflow: "hidden",
        })
      ),
      state(
        "expanded",
        style({
          height: "*",
          opacity: "1",
          overflow: "visible",
        })
      ),
      transition("collapsed <=> expanded", [animate("300ms ease-in-out")]),
    ]),
    trigger("rotateAnimation", [
      state(
        "rotated",
        style({
          transform: "rotate(45deg)",
        })
      ),
      state(
        "notRotated",
        style({
          transform: "rotate(0deg)",
        })
      ),
      transition("notRotated <=> rotated", [animate("0.3s ease-in-out")]),
    ]),
  ],
})
export class MobileHeaderComponent implements OnInit {
  isExpanded = false;
  public questions: { title: String; body: String }[] = [
    { title: "Question lorem ipsum", body: "Body Lorem Ipsum" },
    { title: "Question lorem ipsum", body: "Body Lorem Ipsum" },
    { title: "Question lorem ipsum", body: "Body Lorem Ipsum" },
    { title: "Question lorem ipsum", body: "Body Lorem Ipsum" },
    { title: "Question lorem ipsum", body: "Body Lorem Ipsum" },
  ];

  toggleExpand() {
    this.isExpanded = !this.isExpanded;
  }

  ngOnInit() {}
}
<div class="w-screen h-12 sticky top-0 z-50 overflow-x-hidden">
  <div class="w-full h-full flex flex-row justify-between px-8">
    <!-- left -->
    <div class="w-full h-full flex flex-row items-center gap-8 justify-between">
      <img
        class="h-10 cursor-pointer text-white"
        src="./assets/images/menu.svg"
        alt="Bullet Point Icon"
        [style.transform]="isExpanded ? 'rotate(45deg)' : 'none'"
        [@rotateAnimation]="isExpanded ? 'rotated' : 'notRotated'"
        (click)="toggleExpand()"
      />

      <!-- right -->
      <div class="w-1/2 h-full flex flex-row-reverse items-center">
        <p class="flex text-center text-primaryText font-semibold text-xl">
          Templett
        </p>
      </div>
    </div>
  </div>
</div>
