import { Component, ElementRef } from "@angular/core";

@Component({
  selector: "app-footer",
  templateUrl: "./footer.component.html",
  styleUrls: ["./footer.component.scss"],
})
export class FooterComponent {
  constructor(private el: ElementRef) {}
  scrollToSection(targetSectionId: string) {
    const targetEl =
      this.el.nativeElement.ownerDocument.getElementById(targetSectionId);
    if (targetEl) {
      targetEl.scrollIntoView({ behavior: "smooth" });
    }
  }
}
<div
  class="h-[40vh] flex flex-col w-full bg-primary footer justify-evenly items-center"
>
  <div class="flex">
    <p class="text-primaryText text-lg font-semibold">Hör av er till oss!</p>
  </div>
  <div
    class="flex text-primaryText flex-row w-[80%] max-w-[600px] justify-evenly"
  >
    <p>1</p>
    <p>2</p>
    <p>3</p>
  </div>
  <div class="flex">
    <p class="text-primaryText text-lg font-semibold">
      © Templett 2023-2023 Göteborg
    </p>
  </div>
  <div class="backToTopButton cursor-pointer" (click)="scrollToSection('body')">
    <img class="invert" src="./assets/images/chevron-up.svg" />
  </div>
</div>
