import { Component, ElementRef, ViewChild } from "@angular/core";
import {
  trigger,
  state,
  style,
  animate,
  transition,
} from "@angular/animations";

@Component({
  selector: "app-mobile-header",
  templateUrl: "./mobile-header.component.html",
  styleUrls: ["./mobile-header.component.scss"],
  animations: [
    // Your animations here
  ],
})
export class MobileHeaderComponent {
  isExpanded = false;
  @ViewChild("navToggle") navToggle!: ElementRef;

  toggleExpand() {
    this.isExpanded = !this.isExpanded;
    this.toggleBodyScroll(!this.isExpanded);
    this.toggleHamburger();
  }

  toggleBodyScroll(enableScroll: boolean) {
    if (enableScroll) {
      document.body.classList.remove("no-scroll");
    } else {
      document.body.classList.add("no-scroll");
    }
  }

  toggleHamburger() {
    const bars = document.querySelectorAll(".bar");
    bars.forEach((bar) => bar.classList.toggle("x"));
  }

  ngAfterViewInit() {
    if (this.navToggle && this.navToggle.nativeElement) {
      this.navToggle.nativeElement.addEventListener("click", () => {
        this.toggleHamburger();
      });
    }
  }
}
<div class="w-screen h-16 sticky top-0 z-50 overflow-x-hidden">
  <div class="w-full h-full flex flex-row justify-between px-4">
    <!-- left -->
    <div class="w-full h-full flex flex-row items-center gap-8 justify-between">
      <img
        class="h-10 cursor-pointer invert"
        src="./assets/images/menu.svg"
        alt="Menu Icon"
        [style.transform]="isExpanded ? 'rotate(45deg)' : 'none'"
        (click)="toggleExpand()"
      />
      <a class="nav-toggle">
        <span class="bar"></span>
        <span class="bar"></span>
        <span class="bar"></span>
      </a>

      <!-- right -->
      <div class="w-1/2 h-full flex flex-row-reverse items-center">
        <p class="flex text-center text-primaryText font-semibold text-2xl">
          Templett
        </p>
      </div>
    </div>
  </div>
</div>

<app-sidenav [isExpanded]="isExpanded"></app-sidenav>
