import { Component, ElementRef, HostListener } from "@angular/core";

@Component({
  selector: "app-header",
  templateUrl: "./header.component.html",
  styleUrls: ["./header.component.scss"],
})
export class HeaderComponent {
  constructor(private el: ElementRef) {}
  scrollToSection(targetSectionId: string) {
    const targetEl =
      this.el.nativeElement.ownerDocument.getElementById(targetSectionId);
    if (targetEl) {
      targetEl.scrollIntoView({ behavior: "smooth" });
    }
  }

  isScrolledPastHero = false;

  @HostListener("window:scroll", [])
  onWindowScroll() {
    const scrollPosition =
      window.scrollY ||
      document.documentElement.scrollTop ||
      document.body.scrollTop ||
      0;
    const scroll320vh = 3.2 * window.innerHeight; // Calculate 320vh in pixels

    // Check if scroll position is greater than or equal to 320vh
    this.isScrolledPastHero = scrollPosition >= scroll320vh;
  }
}
<div
  class="w-screen h-16 sticky top-0 z-50 overflow-x-hidden hidden lg:block"
  [class.background-color]="isScrolledPastHero"
>
  <div class="w-full h-full flex flex-row justify-between px-8">
    <!-- left -->
    <div class="w-1/2 h-full flex flex-row items-center gap-8">
      <p
        class="flex text-center text-primaryText font-semibold text-xl cursor-pointer hover:underline"
        (click)="scrollToSection('aboutUsSection')"
      >
        Om
      </p>
      <p
        class="flex text-center text-primaryText font-semibold text-xl cursor-pointer hover:underline"
        (click)="scrollToSection('offersSection')"
      >
        Våra produkter
      </p>
      <p
        class="flex text-center text-primaryText font-semibold text-xl cursor-pointer hover:underline"
        (click)="scrollToSection('faqSection')"
      >
        Frågor?
      </p>
      <p
        class="flex text-center text-primaryText font-semibold text-xl cursor-pointer hover:underline"
        (click)="scrollToSection('aboutUsSection')"
      >
        Kontakta oss
      </p>
    </div>

    <!-- right -->
    <div class="w-1/2 h-full flex flex-row-reverse items-center">
      <p class="flex text-center text-primaryText font-semibold text-xl">
        Templett
      </p>
    </div>
  </div>
</div>
