import { Component, ElementRef } from "@angular/core";

@Component({
  selector: "app-footer",
  templateUrl: "./footer.component.html",
  styleUrls: ["./footer.component.scss"],
})
export class FooterComponent {
  constructor(private el: ElementRef) {}

  showPhoneInfo: boolean = false;
  showEmailInfo: boolean = false;

  togglePhoneInfo() {
    this.showPhoneInfo = !this.showPhoneInfo;
    // Hide email info when showing phone info
    this.showEmailInfo = false;
  }

  toggleEmailInfo() {
    this.showEmailInfo = !this.showEmailInfo;
    // Hide phone info when showing email info
    this.showPhoneInfo = false;
  }
  scrollToSection(targetSectionId: string) {
    const targetEl =
      this.el.nativeElement.ownerDocument.getElementById(targetSectionId);
    if (targetEl) {
      targetEl.scrollIntoView({ behavior: "smooth" });
    }
  }
}
<div
  class="h-[40vh] flex flex-col w-full bg-primary footer justify-evenly items-center"
>
  <div class="flex">
    <p class="text-primaryText text-xl font-semibold">Hör av er till oss!</p>
  </div>
  <div
    class="flex text-primaryText flex-row w-[80%] max-w-[600px] justify-evenly"
  >
    <div class="backToTopButton cursor-pointer" (click)="togglePhoneInfo()">
      <img class="invert" src="./assets/images/phone.svg" />
    </div>

    <div class="backToTopButton cursor-pointer" (click)="toggleEmailInfo()">
      <img class="invert" src="./assets/images/email.svg" />
    </div>

    <a
      class="backToTopButton cursor-pointer"
      href="https://www.linkedin.com/in/erik-junghahn-751a3215b/"
      target="_blank"
    >
      <img class="invert opacity-75" src="./assets/images/linkedin.svg" />
    </a>
  </div>
  <div class="overlay" *ngIf="showPhoneInfo">
    <p class="text-primaryText font-semibold">+46 793 479 334</p>
  </div>
  <div class="overlay" *ngIf="showEmailInfo">
    <p class="text-primaryText font-semibold">example@example.com</p>
  </div>
  <div class="flex">
    <p class="text-primaryText text-lg font-semibold">
      © Templett 2023-2023 Göteborg
    </p>
  </div>
  <div class="backToTopButton cursor-pointer" (click)="scrollToSection('body')">
    <img class="invert" src="./assets/images/chevron-up.svg" />
  </div>
</div>
