import {
  Component,
  ElementRef,
  EventEmitter,
  HostListener,
  Input,
  Output,
} from "@angular/core";
import {
  trigger,
  state,
  style,
  animate,
  transition,
} from "@angular/animations";

@Component({
  selector: "app-sidenav",
  templateUrl: "./sidenav.component.html",
  styleUrls: ["./sidenav.component.scss"],
  animations: [
    trigger("sidenavAnimation", [
      state(
        "collapsed",
        style({
          transform: "translateX(-100%)",
          opacity: 0,
        })
      ),
      state(
        "expanded",
        style({
          transform: "translateX(0)",
          opacity: 0.95,
        })
      ),
      transition("collapsed => expanded", animate("300ms ease-in")),
      transition("expanded => collapsed", animate("300ms ease-out")),
    ]),
  ],
})
export class SidenavComponent {
  @Input() isExpanded = false;
  @Output() isExpandedChange = new EventEmitter<boolean>();
  constructor(private el: ElementRef) {}

  scrollToSection(targetSectionId: string) {
    this.isExpanded = !this.isExpanded;
    this.isExpandedChange.emit(this.isExpanded);
    document.body.classList.remove("no-scroll");

    const checkboxEl = this.el.nativeElement.ownerDocument.getElementById(
      "animatedCheck"
    ) as HTMLInputElement;
    if (checkboxEl) {
      checkboxEl.checked = !checkboxEl.checked;
    }

    const targetEl =
      this.el.nativeElement.ownerDocument.getElementById(targetSectionId);
    if (targetEl) {
      //targetEl.scrollIntoView({ behavior: "smooth" });
    }
  }
}
<div
  class="sidenav p-8"
  [@sidenavAnimation]="isExpanded ? 'expanded' : 'collapsed'"
>
  <!-- Your side navigation menu items -->
  <ul>
    <li (click)="scrollToSection('aboutUsSection')">
      <p class="text-primaryText text-lg py-2 cursor-pointer">Om</p>
    </li>
    <li>
      <p class="text-primaryText text-lg py-2 cursor-pointer">Våra produkter</p>
    </li>
    <li>
      <p class="text-primaryText text-lg py-2 cursor-pointer">
        Varför Templett?
      </p>
    </li>
    <li>
      <p class="text-primaryText text-lg py-2 cursor-pointer">
        Frågor och svar
      </p>
    </li>
    <li>
      <p class="text-primaryText text-lg py-2 cursor-pointer">Kontakta osss</p>
    </li>

    <!-- Add other menu items -->
  </ul>
</div>
