import { Component, Input, OnInit } from "@angular/core";
import {
  trigger,
  state,
  style,
  animate,
  transition,
} from "@angular/animations";

@Component({
  selector: "app-mobile-header",
  templateUrl: "./mobile-header.component.html",
  styleUrls: ["./mobile-header.component.scss"],
  animations: [
    trigger("expandCollapse", [
      state(
        "collapsed",
        style({
          height: "0",
          opacity: "0",
          overflow: "hidden",
        })
      ),
      state(
        "expanded",
        style({
          height: "*",
          opacity: "1",
          overflow: "visible",
        })
      ),
      transition("collapsed <=> expanded", [animate("300ms ease-in-out")]),
    ]),
    trigger("rotateAnimation", [
      state(
        "rotated",
        style({
          transform: "rotate(45deg)",
        })
      ),
      state(
        "notRotated",
        style({
          transform: "rotate(0deg)",
        })
      ),
      transition("notRotated <=> rotated", [animate("0.3s ease-in-out")]),
    ]),
  ],
})
export class MobileHeaderComponent implements OnInit {
  @Input() isExpanded = false;

  toggleExpand() {
    this.isExpanded = !this.isExpanded;
    this.toggleBodyScroll(!this.isExpanded);
  }

  onExpandChange(value: boolean) {
    this.isExpanded = value; // Update the value in the parent component
  }
  toggleBodyScroll(enableScroll: boolean) {
    if (enableScroll) {
      document.body.classList.remove("no-scroll");
    } else {
      document.body.classList.add("no-scroll");
    }
  }

  ngOnInit() {}
}
<div class="w-screen h-16 sticky top-0 z-50 overflow-x-hidden">
  <div class="w-full h-full flex flex-row justify-between px-4">
    <!-- left -->
    <div class="w-full h-full flex flex-row items-center gap-8 justify-between">
      <div class="menu-body">
        <label for="animatedCheck">
          <input type="checkbox" id="animatedCheck" (click)="toggleExpand()" />
          <span></span>
          <span></span>
          <span></span>
        </label>
      </div>

      <!-- right -->
      <div class="w-1/2 h-full flex flex-row-reverse items-center">
        <p class="flex text-center text-primaryText font-semibold text-2xl">
          Templett
        </p>
      </div>
    </div>
  </div>
</div>

<app-sidenav
  [isExpanded]="isExpanded"
  (isExpandedChange)="onExpandChange($event)"
></app-sidenav>
